import React, { Fragment, useEffect, useState } from 'react'
import { Box, InputBase, Typography, Button } from '@material-ui/core'
import { useHistory } from "react-router-dom";
import { fade, makeStyles } from '@material-ui/core/styles'
import SearchIcon from '@material-ui/icons/Search'
import UtilitiesItem from '../AppsComp/UtilitiesItem'
import ListFilter from '../AppsComp/ListFilter'
import SubmitBtn from '../AppsComp/SubmitBtn'
import useWindowDimensions from '../../hooks/useWindowDimensions'
import HostingItem from './HostingItem'
import AddNewSite from './AddNewSiteBtn'
import { getMyHostedApps, deleteMyHostedApp } from '../../service/SnSkappService';
import SnInfoModal from '../Modals/SnInfoModal';
import { isStrInObj } from '../../utils/SnUtility';
import { useDispatch } from 'react-redux';
import { setLoaderDisplay } from '../../redux/action-reducers-epic/SnLoaderAction';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';

const useStyles = makeStyles(theme => (
    {
        search: {
            position: 'relative',
            borderRadius: theme.shape.borderRadius,
            backgroundColor: fade('#fff', 1),
            '&:hover': {
                backgroundColor: fade("#fff", 0.9),
            },
            marginRight: theme.spacing(2),
            width: '100%',
            [theme.breakpoints.up('sm')]: {
                marginLeft: theme.spacing(3),
                width: 'auto',
            },
            color: '#8B9DA5',
            boxShadow: '0px 1px 2px #15223214',
            border: '1px solid #7070701A;',

            marginLeft: '16px!important',
            '@media (max-width: 1650px)': {
                width: 'auto'
            },

        },
        modal: {
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
        },
        closeBtn: {
            border: '1px solid #1DBF73',
            borderRadius: '5px',
            boxShadow: '0px 2px 5px #15223221',
            height: 48,
            marginTop: '2rem',
            textTransform: 'none'
        },
        okBtn: {
            border: '1px solid #ea052f',
            marginRight: '10px',
            borderRadius: '5px',
            boxShadow: '0px 2px 5px #15223221',
            height: 48,
            marginTop: '2rem',
            textTransform: 'none'
        },
        modalTitle: {
            fontSize: 32,
            color: '#333333',
            fontWeight: 700,
            marginBottom: '1rem'
        },
        shareCardContainer: {
            background: '#fff',
            boxShadow: '0px 2px 5px #15223221',
            borderRadius: 15,
            padding: '48px 60px',
            '@media only screen and (max-width: 575px)': {
                padding: '40px 20px',
                paddingTop: '50px'
            },
            '&:focus': {
                outline: 0,
                border: 0
            },
            width: '90%',
            maxWidth: 500,
            '& p': {
                color: '#5A607F',
                marginBottom: '5px'
            },
            '& .s-links-title': {
                marginTop: '.4rem'
            },
            '& a': {
                marginRight: '1rem',
                '&:focus': {
                    textDecoration: 'none',
                    opacity: .8,
                    transition: '.25s ease'
                }
            }
    
        },
        searchIcon: {
            padding: theme.spacing(0, 2),
            height: '100%',
            position: 'absolute',
            pointerEvents: 'none',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            color: '#B4C6CC'
